	"setembro": "9",
	"set": "9",
	"outubro": "'10",
	"out": "10",
	"novembro": "11",
	"nov": "11",
	"dezembro": "12",
	"dez": "12"
    };
    birthdate = (birthdate).replace(/\bde\b/g,'').split(' ');
    birthdate = birthdate.filter(x => x != '');
