    return birthdate.join('/');
}

function run(birthdate, currentYear) {
    currentYear = currentYear.slice(2);	
	let regexLetter = /[a-zA-Z]/g;

	if(!regexLetter.test(birthdate)) {
    	birthdate = birthdate.replace(/\/|-|\.|\ /g, '/');
	} else {
        birthdate=formatDateWithWrittenMonth(birthdate);
    }

    let birthdateYear = birthdate.split('/')[2];
    if(birthdateYear.length == 2){
        birthdate = birthdate.split('/');

