function run(currentTime, startedTime) {
    const limitMinute = 5;

    const startedTimeHour = parseInt(startedTime.split(':')[0]);
    const startedTime = parseInt(startedTime.split(':')[1]);
    const currentTimeHour = parseInt(currentTime.split(':')[0]);
    const currentTime = parseInt(currentTime.split(':')[1]);

    if (startedTimeHour == currentTimeHour || (startedTimeHour < currentTimeHour && currentTime < limitMinute)) {
